mapper.select:
select * from (select rownum as num, inter_no, NVL(inter_status,'돌보미배정중') as inter_status, to_char(inter_date, 'YYYY-MM-DD ') as inter_date, inter_time || '시' as inter_time, nvl(inter_bomi_name,'미배정') as inter_bomi_name, INTER_CENTER, to_char(inter_req_date, 'YYYY-MM-DD ') as inter_req_date from(select * from interview_maintain_view where inter_user_id = #{userid}
and inter_bomi_name like '%'||#{dolname}||'%'
and inter_date between #{date_begin} and #{date_end}
and inter_req_date between #{date_begin} and #{date_end}
and inter_status like '%'||#{center}||'%'
order by inter_req_date)) order by num desc

mapper.update:
update interview_maintain_view

	set inter_date = #{inter_date}


	set inter_time = #{inter_time}


	set inter_status = decode(#{inter_status},'1',null,'2','면접예정','3',
	'면접완료', '0', '면접취소')

where inter_no = #{inter_no}
